public static class MiddlewareExtensions { public static IApplicationBuilder UseWarmup(this IApplicationBuilder app) { return app.UseMiddleware<WarmupMiddleware>(); } public static IApplicationBuilder UseStrictTransportSecurity(this IApplicationBuilder app) { return app.UseMiddleware<StrictTransportSecurityMiddleware>(); } public static IApplicationBuilder UseOrchard(this IApplicationBuilder app) { return app.UseMiddleware<OrchardMiddleware>(); } }
